mapper.select:
SELECT COUNT(*)
  FROM member
WHERE MEM_ID = #{memId};

SELECT COUNT(*)
  FROM member
WHERE MEM_NICK = #{memNick};

SELECT *
  FROM member
 WHERE MEM_ID = #{memId}
   AND MEM_NICK = #{memNick}
   AND MEM_EMAIL = #{memEmail}

mapper.update:
UPDATE member
   SET MEM_PASSWORD = #{memPassword}
 WHERE MEM_ID = #{memId}

UPDATE member
    SET LOGIN_FAIL_COUNT = LOGIN_FAIL_COUNT + 1
WHERE MEM_ID = #{memId}
